    public function createFooter()
    {
        // Set up the WordPress query.
        $this->footerId = e2e_create_post(
            [
                'post_title' => 'test-themeEditor-popupBuilder-footer',
                'post_content' => 'This is footer',
                'post_status' => 'publish',
                'post_type' => 'vcv_footers',
            ]
        );
    }

    public function createPost()
    {
        $hubAddonsHelper = vchelper('HubAddons');

        // Set up the WordPress query.
        $this->postId = e2e_create_post(
            [
                'post_title' => 'test-themeEditor-popupBuilder-page',
                'post_content' => sprintf(
                    '<!--vcv no format-->This is post with popup on page load..
                    Popup ID: <span id="php-e2e-popup-id">%s</span>
                    <!--vcv no format-->',
                    $this->popupId
                ),
                'post_status' => 'publish',
                'post_type' => 'page',
                'meta_input' => [
                    'vcv-be-editor' => 'fe',
                    '_vcv-page-template' => 'header-footer-layout',
                    '_vcv-page-template-type' => 'vc-theme',
                    '_vcv-page-template-stretch' => '0',
                    '_vcv-pageTitleDisabled' => '',
                    '_vcv-HeaderTemplateId' => $this->headerId,
                    '_vcv-FooterTemplateId' => $this->footerId,
                    '_vcv-SidebarTemplateId' => '',
                    'vcvSourceAssetsFiles' => [
                        'jsBundles' => [
                            $hubAddonsHelper->getAddonUrl(
                                'popupBuilder/popupBuilder/public/dist/popupOnPageLoad.bundle.js'
                            ),
                        ],
                        'cssBundles' => [
                            $hubAddonsHelper->getAddonUrl(
                                'popupBuilder/popupBuilder/public/dist/popup.bundle.css'
                            ),
                        ],
                    ],
                    '_vcv-settingsPopup' => [
                        'popupOnPageLoad' => [
                            'id' => $this->popupId,
                        ],
                    ],
                ],
            ]
        );
    }

    public function setupPage()
    {
        // Create rewrite rules, to avoid 404 issues and extra redirects
        e2e_add_rewrite_rules();

        // Setup global query
        wp(['p' => $this->postId, 'post_type' => 'page']);

        // Load the theme template.
        require_once ABSPATH . WPINC . '/template-loader.php';
    }
}
